import React, { useEffect, useState } from 'react';
import { Checkbox } from '@mui/material';
import { Check, X, Pen } from 'lucide-react'
import { styled } from '@mui/system';

const CustomCheckbox = styled(Checkbox)({
  '& .MuiSvgIcon-root': { fontSize: 28 },
  '&.Mui-checked': {
    color: '#48b574',
  },
});

interface ListingCardProps {
  title?: string;
  companyName?: string;
  description?: string;
  status?: string;
  // POSSIBILITIES FOR STATUS LEGEND
  // WATCHING => GRAY
  // APPLIED => GREEN
  // INTERVIEWING => YELLOW
  // REJECTED => RED

  isApplied?: boolean;

  // double check for type
  applicationDate?: string;
  interviewDate?: string;
  offerDate?: string;
  tags?: string[];
}

const ListingCard: React.FC<ListingCardProps> = ({ title, companyName, description, status, isApplied }) => {
  const [applied, setApplied] = useState(false);

  // useEffect(() => {
  //   setApplied(isApplied);
  // }, [isApplied]);

  const handleApply = () => {
    setApplied(!applied);
  };

  return (
    <div className="relative mx-10 my-8">
      <div className="flex items-center justify-between bg-main-gray p-4 rounded-2xl shadow-2xl w-full py-2 relative px-10">
        <div className="flex items-center justify-center flex-1 relative">
          {title}
        </div>
        <div className="flex items-center justify-center flex-1 relative">
          {companyName}
        </div>
        <div className="flex items-center justify-center flex-1">
          <div className="flex items-center justify-center bg-green-200 rounded-full w-8 h-8 ml-1 shadow-xl"></div>
        </div>
        <div className="flex items-center justify-center flex-1">
          <CustomCheckbox
            checked={applied}
            onChange={handleApply}
            inputProps={{ 'aria-label': 'controlled' }}
            className="ml-1.5"
          />
        </div>
        <div className="absolute right-5 top-1/2 transform -translate-y-1/2 flex items-center space-x-3">
          <button className="flex items-center justify-center bg-main-green hover:bg-green-200 rounded-full p-2 shadow-lg">
            <Pen size={20} />
          </button>
          <div className="flex items-center justify-center bg-red-400 hover:bg-red-300 rounded-full p-2 shadow-lg cursor-pointer">
            <X size={20} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListingCard;
