import React, {useEffect, useState } from 'react';

interface ListingCardProps {
  title: string;
  companyName: string;
  description?: string;
  status: string; 
  // POSSIBILITIES FOR STATUS LEGEND
  // WATCHING => GRAY
  // APPLIED => GREEN
  // INTERVIEWING => YELLOW
  // REJECTED => RED

  isApplied?: boolean;

  // double check for type
  applicationDate?: string;
  interviewDate?: string;
  offerDate?: string;
  tags?: string[];
}

const ListingCard: React.FC<ListingCardProps> = ({ title, companyName, description, status, isApplied }) => {
  const [applied, setApplied] = useState(isApplied);

  useEffect(() => {
    setApplied(isApplied);
  }, [isApplied]);



  const handleApply = () => {
    setApplied(true);
  };

  return (
    <div className="h-full w-[400px] bg-main-gray rounded-lg shadow-lg">
      <div className="flex flex-row">
        <div>
          <h1>{title}</h1>
        </div>
        <div>
          <h1>{companyName}</h1>
        </div>
        <div>
          <button className="p-4 bg-black"> </button>
        </div>
        <div>
          <h1></h1>
        </div>


      </div>
    </div>

  );
};
