import React, { useState } from 'react';
import { Checkbox } from '@mui/material';
import { Check, X, Pen } from 'lucide-react'
import { styled } from '@mui/system';

const CustomCheckbox = styled(Checkbox)({
  '& .MuiSvgIcon-root': { fontSize: 28 }, 
  '&.Mui-checked': {
    color: '#48b574',
  },
});

interface ListingCardProps {
  title?: string;
  companyName?: string;
  description?: string;
  status?: string;
  isApplied?: boolean;
  applicationDate?: string;
  interviewDate?: string;
  offerDate?: string;
  tags?: string[];
}

const ListingCard: React.FC<ListingCardProps> = ({ title, companyName, description, status, isApplied }) => {
  const [applied, setApplied] = useState(false);

  const handleApply = () => {
    setApplied(!applied);
  };

  return (
    <div className="relative py-8 px-16">
      <div className="relative p-2 my-4 bg-main-gray rounded-2xl shadow-2xl">
        <div className="relative">
          <div className="absolute left-0 top-1/2 transform -translate-y-1/2">
            <h1 className="text-xl font-medium">{title || 'Temporary Title'}</h1>
          </div>
          <div className="absolute left-1/4 top-1/2 transform -translate-y-1/2">
            <h1 className="text-xl font-medium">{companyName || 'Google'}</h1>
          </div>
          <div className="absolute left-1/2 top-1/2 transform -translate-y-1/2">
            <button className="p-5 rounded-full bg-green-200 shadow-xl"> </button>
          </div>
          <div className="absolute left-3/4 top-1/2 transform -translate-y-1/2">
            <CustomCheckbox
              checked={applied}
              onChange={handleApply}
              inputProps={{ 'aria-label': 'controlled' }}
            />
          </div>
          <div className="absolute right-0 top-1/2 transform -translate-y-1/2 flex space-x-2">
            <button className="p-2 shadow-md bg-main-green rounded-full">
              <Pen size={20}></Pen>
            </button>
            <button className="p-2 shadow-md bg-red-400 rounded-full">
              <X size={20}></X>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListingCard;
