import React, {useEffect, useState } from 'react';

interface ListingCardProps {
  title?: string;
  companyName?: string;
  description?: string;
  status?: string; 
  // POSSIBILITIES FOR STATUS LEGEND
  // WATCHING => GRAY
  // APPLIED => GREEN
  // INTERVIEWING => YELLOW
  // REJECTED => RED

  isApplied?: boolean;

  // double check for type
  applicationDate?: string;
  interviewDate?: string;
  offerDate?: string;
  tags?: string[];
}

const ListingCard: React.FC<ListingCardProps> = ({ title, companyName, description, status, isApplied }) => {
  const [applied, setApplied] = useState(isApplied);

  useEffect(() => {
    setApplied(isApplied);
  }, [isApplied]);



  const handleApply = () => {
    setApplied(true);
  };

  return (
    <div className="py-8 px-16">
      <div className="p-2 my-4 bg-main-gray rounded-2xl shadow-2xl">
        <div className="flex flex-row h-[100px] justify-evenly items-center">
          <div>
            <h1>Temporary Title</h1>
          </div>
          <div>
            <h1>Google</h1>
          </div>
          <div>
            <button className="p-5 rounded-full bg-green-200 shadow-xl"> </button>
          </div>
          <div>
            <h1> LOL </h1>
          </div>


        </div>
      </div>
    </div>


  );
};

export default ListingCard;
