import React, { useState, useEffect, useRef } from 'react';
import { Box, Fab, TextField, Card, CardContent, Button } from '@mui/material';
import ListingCard from './ListingCard';
import AddIcon from '@mui/icons-material/Add';
import CheckIcon from '@mui/icons-material/Check';

const API_URL = 'http://localhost:8080/jobs'; // Change at production

interface Listing {
  title: string;
  company: string;
  isChecked: boolean;
  isEditing: boolean;
  status: 'red' | 'green' | 'yellow';
}

const ListingCardGrid: React.FC = () => {
  const [listings, setListings] = useState<Listing[]>([]);
  const [loading, setLoading] = useState(true);
  const [isAdding, setIsAdding] = useState(false);
  const [newListing, setNewListing] = useState<Listing>({
    title: '',
    company: '',
    isChecked: false,
    isEditing: true,
    status: 'yellow',
  });
  const formRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const response = await fetch(API_URL);
        const data = await response.json();
        setListings(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching listings:', error);
        setLoading(false);
      }
    };

    fetchListings();
  }, []);

  const handleAddListing = async () => {
    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newListing),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setListings((prevListings) => [...prevListings, data]);
      setIsAdding(false);
    } catch (error) {
      console.error('Error adding new listing:', error);
    }
  };

  const handleCancel = () => {
    setIsAdding(false);
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (formRef.current && !formRef.current.contains(event.target as Node)) {
        handleCancel();
      }
    };

    if (isAdding) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isAdding]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Box>
        {listings.map((listing, index) => (
          <ListingCard
            key={index}
            title={listing.title}
            company={listing.company}
            isChecked={listing.isChecked}
            isEditing={listing.isEditing}
            color={listing.status}
          />
        ))}
        {isAdding && (
          <Card ref={formRef} sx={{ marginBottom: 2 }}>
            <CardContent>
              <TextField
                label="Title"
                value={newListing.title}
                onChange={(e) =>
                  setNewListing({ ...newListing, title: e.target.value })
                }
                fullWidth
                margin="normal"
              />
              <TextField
                label="Company"
                value={newListing.company}
                onChange={(e) =>
                  setNewListing({ ...newListing, company: e.target.value })
                }
                fullWidth
                margin="normal"
              />
              <Button onClick={handleAddListing} color="primary" variant="contained">
                Add Listing
              </Button>
            </CardContent>
          </Card>
        )}
      </Box>
      <Fab
        color="primary"
        aria-label="add"
        onClick={() => setIsAdding(true)}
        sx={{
          position: 'fixed',
          bottom: 40,
          right: 40,
          backgroundColor: 'white',
          border: '4px solid #67FFA4',
          color: 'black',
          '&:hover': {
            backgroundColor: 'white',
          },
        }}
      >
        {isAdding ? <CheckIcon sx={{ color: 'black' }} /> : <AddIcon sx={{ color: 'black' }} />}
      </Fab>
    </div>
  );
};

export default ListingCardGrid;
