import React, { useState, useEffect, useRef } from 'react';
import { Box, Fab, TextField, Card, CardContent, IconButton, Typography, Checkbox } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import CheckIcon from '@mui/icons-material/Check';
import CloseIcon from '@mui/icons-material/Close';
import EditIcon from '@mui/icons-material/Edit';
import { styled } from '@mui/material/styles';
import './font.css';

const API_URL = 'http://localhost:8080/jobs'; // Change at production

interface Listing {
  title: string;
  company: string;
  isChecked: boolean;
  isEditing: boolean;
  status: 'red' | 'green' | 'yellow';
}

const colorMap = {
  red: '#EC3737',
  green: '#67FFA4',
  yellow: '#EAFF67',
};

const StyledTextField = styled(TextField)(({ theme }) => ({
  '& .MuiInput-underline:before': {
    borderBottom: '1px solid rgba(0, 0, 0, 0.42)',
    transition: 'border-bottom 1s ease',
  },
  '& .MuiInput-underline:after': {
    borderBottom: `2px solid ${theme.palette.primary.main}`,
    transition: 'border-bottom 1s ease',
  },
}));

const ListingCard: React.FC<Listing> = ({
  title,
  company,
  isChecked: initialChecked,
  isEditing: initialEditing,
  status,
}) => {
  const [isEditing, setIsEditing] = useState(initialEditing);
  const [editedTitle, setEditedTitle] = useState(title);
  const [editedCompany, setEditedCompany] = useState(company);
  const [isChecked, setIsChecked] = useState(initialChecked);

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    // Handle save logic, such as updating state or making API calls
  };

  const handleDeleteClick = () => {
    // Handle delete logic
  };

  return (
    <Box mx={10} mt={4.5} position="relative">
      <Box
        display="flex"
        alignItems="center"
        justifyContent="space-between"
        bgcolor="#D9D9D9"
        p={1}
        borderRadius={5}
        width="100%"
        height={70}
        position="relative"
        sx={{ boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)' }}
      >
        {isEditing ? (
          <>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
                width: '80%',
                position: 'relative',
              }}
            >
              <Box
                sx={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  width: '10px',
                  height: '100%',
                  backgroundColor: '#D9D9D9',
                  zIndex: 1,
                }}
              />
              <Box
                sx={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '10px',
                  height: '100%',
                  backgroundColor: '#D9D9D9',
                  zIndex: 1,
                }}
              />
              <StyledTextField
                variant="standard"
                value={editedTitle}
                onChange={(e) => setEditedTitle(e.target.value)}
                sx={{ flex: 1 }}
                inputProps={{ sx: { textAlign: 'center' }, maxLength: 28 }}
              />
            </Box>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
                width: '80%',
                position: 'relative',
              }}
            >
              <Box
                sx={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '10px',
                  height: '100%',
                  backgroundColor: '#D9D9D9',
                  zIndex: 1,
                }}
              />
              <Box
                sx={{
                  position: 'absolute',
                  top: 0,
                  right: 0,
                  width: '10px',
                  height: '100%',
                  backgroundColor: '#D9D9D9',
                  zIndex: 1,
                }}
              />
              <StyledTextField
                variant="standard"
                value={editedCompany}
                onChange={(e) => setEditedCompany(e.target.value)}
                sx={{ flex: 1, zIndex: 0 }}
                inputProps={{ sx: { textAlign: 'center' }, maxLength: 20 }}
              />
            </Box>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
              }}
            >
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  bgcolor: colorMap[status],
                  borderRadius: '50%',
                  width: 30,
                  height: 30,
                  ml: 1,
                  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
                }}
              />
            </Box>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
              }}
            >
              <Checkbox
                sx={{
                  color: 'gray',
                  ml: 1.5,
                  '&.Mui-checked': {
                    color: 'gray',
                  },
                  '& .MuiSvgIcon-root': {
                    fontSize: 24,
                  },
                }}
                checked={isChecked}
                onChange={(e) => setIsChecked(e.target.checked)}
              />
            </Box>
          </>
        ) : (
          <>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
              }}
            >
              <Typography
                className="rubik"
                variant="body1"
                sx={{
                  textAlign: 'center',
                  fontWeight: 600,
                  color: '#3D3D3D',
                  fontSize: 15,
                  maxWidth: 190,
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                  overflow: 'hidden',
                }}
              >
                {editedTitle}
              </Typography>
            </Box>
            <Typography
              className="rubik"
              variant="body1"
              sx={{
                flex: 1,
                textAlign: 'center',
                fontWeight: 600,
                color: '#3D3D3D',
                fontSize: 15,
                whiteSpace: 'nowrap',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                maxWidth: '100%',
              }}
            >
              {editedCompany}
            </Typography>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
              }}
            >
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  bgcolor: colorMap[status],
                  borderRadius: '50%',
                  width: 30,
                  height: 30,
                  ml: 1,
                  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
                }}
              />
            </Box>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
              }}
            >
              <Checkbox
                sx={{
                  color: 'gray',
                  ml: 1.5,
                  '&.Mui-checked': {
                    color: 'gray',
                  },
                  '& .MuiSvgIcon-root': {
                    fontSize: 24,
                  },
                  '&:hover': {
                    backgroundColor: 'transparent',
                  },
                  '& .MuiTouchRipple-root': {
                    display: 'none',
                  },
                  cursor: 'default',
                  transition: 'none',
                }}
                checked={isChecked}
                readOnly
              />
            </Box>
          </>
        )}
        <Box
          sx={{
            position: 'absolute',
            right: 20,
            top: '50%',
            transform: 'translateY(-50%)',
            display: 'flex',
            alignItems: 'center',
          }}
        >
          <IconButton
            onClick={isEditing ? handleSaveClick : handleEditClick}
            sx={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              bgcolor: '#64CEBB',
              borderRadius: '50%',
              width: 19,
              height: 19,
              ml: 1,
            }}
          >
            {isEditing ? (
              <CheckIcon sx={{ maxWidth: 12, ml: '0.79px' }} />
            ) : (
              <EditIcon sx={{ maxWidth: 12, ml: '0.79px' }} />
            )}
          </IconButton>
          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              bgcolor: '#B76767',
              borderRadius: '50%',
              width: 19,
              height: 19,
              ml: 1,
            }}
          >
            <CloseIcon
              onClick={handleDeleteClick}
              sx={{ maxWidth: 16, ml: '0.8px', cursor: 'pointer' }}
            />
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

const ListingCardGrid: React.FC = () => {
  const [listings, setListings] = useState<Listing[]>([]);
  const [loading, setLoading] = useState(true);
  const [isAdding, setIsAdding] = useState(false);
  const [newListing, setNewListing] = useState<Listing>({
    title: '',
    company: '',
    isChecked: false,
    isEditing: true,
    status: 'yellow',
  });
  const formRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const response = await fetch(API_URL);
        const data = await response.json();
        setListings(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching listings:', error);
        setLoading(false);
      }
    };

    fetchListings();
  }, []);

  const handleAddListing = async () => {
    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newListing),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setListings((prevListings) => [...prevListings, data]);
      setIsAdding(false);
    } catch (error) {
      console.error('Error adding new listing:', error);
    }
  };

  const handleCancel = () => {
    setIsAdding(false);
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (formRef.current && !formRef.current.contains(event.target as Node)) {
        handleCancel();
      }
    };

    if (isAdding) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isAdding]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Box>
        {listings.map((listing, index) => (
          <ListingCard
            key={index}
            title={listing.title}
            company={listing.company}
            isChecked={listing.isChecked}
            isEditing={listing.isEditing}
            status={listing.status}
          />
        ))}
        {isAdding && (
          <Card ref={formRef} sx={{ marginBottom: 2 }}>
            <CardContent>
              <StyledTextField
                label="Title"
                variant="standard"
                value={newListing.title}
                onChange={(e) => setNewListing({ ...newListing, title: e.target.value })}
                fullWidth
                margin="normal"
              />
              <StyledTextField
                label="Company"
                variant="standard"
                value={newListing.company}
                onChange={(e) => setNewListing({ ...newListing, company: e.target.value })}
                fullWidth
                margin="normal"
              />
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  bgcolor: colorMap[newListing.status],
                  borderRadius: '50%',
                  width: 30,
                  height: 30,
                  ml: 1,
                  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
                  marginBottom: 2,
                }}
              />
              <IconButton
                onClick={handleAddListing}
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  bgcolor: '#64CEBB',
                  borderRadius: '50%',
                  width: 30,
                  height: 30,
                  marginBottom: 2,
                }}
              >
                <CheckIcon sx={{ color: 'white' }} />
              </IconButton>
            </CardContent>
          </Card>
        )}
      </Box>
      <Fab
        color="primary"
        aria-label="add"
        onClick={() => setIsAdding(true)}
        sx={{
          position: 'fixed',
          bottom: 40,
          right: 40,
          backgroundColor: 'white',
          border: '4px solid #67FFA4',
          color: 'black',
          '&:hover': {
            backgroundColor: 'white',
          },
        }}
      >
        <AddIcon sx={{ color: 'black' }} />
      </Fab>
    </div>
  );
};

export default ListingCardGrid;
