import React, { useEffect, useState, useRef } from 'react';
import { Checkbox } from '@mui/material';
import { Check, X, Pen } from 'lucide-react';
import { styled } from '@mui/system';

const CustomCheckbox = styled(Checkbox)({
  '& .MuiSvgIcon-root': { fontSize: 28 },
  '&.Mui-checked': {
    color: '#48b574',
  },
});

interface ListingCardProps {
  title?: string;
  company?: string;
  description?: string;
  status?: string;
  applicationDate?: string;
  interviewDate?: string;
  offerDate?: string;
  tags?: string[];
}

const ListingCard: React.FC<ListingCardProps> = ({ title, company, description, status, applicationDate, interviewDate, offerDate, tags }) => {
  const [applied, setApplied] = useState(false);
  const [showDetailCard, setShowDetailCard] = useState(false);
  const cardRef = useRef<HTMLDivElement>(null);

  const handleApply = () => {
    setApplied(!applied);
  };

  const toggleDetailCard = () => {
    setShowDetailCard(!showDetailCard);
  };

  const handleClickOutside = (event: MouseEvent) => {
    if (cardRef.current && !cardRef.current.contains(event.target as Node)) {
      setShowDetailCard(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="relative mx-10 mt-8" ref={cardRef}>
      <div 
        className="flex items-center justify-between bg-main-gray p-4 rounded-2xl shadow-2xl w-full cursor-pointer"
        onClick={toggleDetailCard}
      >
        <div className="flex items-center justify-center flex-1">
          <p className="text-xl font-medium">{title}</p>
        </div>
        <div className="flex items-center justify-center flex-1">
          <p className="text-xl font-medium">{company}</p>
        </div>
        <div className="flex items-center justify-center flex-1">
          <div className={`flex items-center justify-center rounded-full w-8 h-8 ml-1 shadow-xl ${statusColor(status)}`}></div>
        </div>
        <div className="flex items-center justify-center flex-1">
          <CustomCheckbox
            checked={applied}
            onChange={handleApply}
            inputProps={{ 'aria-label': 'controlled' }}
            className="ml-1.5"
          />
        </div>
        <div className="absolute right-5 flex items-center space-x-3">
          <button className="flex items-center justify-center bg-main-green hover:bg-green-200 rounded-full p-2 shadow-lg">
            <Pen size={20} />
          </button>
          <div className="flex items-center justify-center bg-red-400 hover:bg-red-300 rounded-full p-2 shadow-lg cursor-pointer">
            <X size={20} />
          </div>
        </div>
      </div>
      {showDetailCard && (
        <div className="bg-main-gray bg-opacity-90 rounded-b-2xl shadow-2xl w-[95%] p-4">
          <p className="text-lg">Description: {description}</p>
          <p className="text-lg">Application Date: {applicationDate}</p>
          <p className="text-lg">Interview Date: {interviewDate}</p>
          <p className="text-lg">Offer Date: {offerDate}</p>
          {tags && (
            <div className="flex flex-wrap mt-2">
              {tags.map((tag, index) => (
                <span key={index} className="bg-blue-200 text-blue-800 rounded-full px-3 py-1 text-sm mr-2 mt-2">
                  {tag}
                </span>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const statusColor = (status: string | undefined) => {
  switch (status) {
    case 'WATCHING':
      return 'bg-gray-200';
    case 'APPLIED':
      return 'bg-green-200';
    case 'INTERVIEWING':
      return 'bg-yellow-200';
    case 'REJECTED':
      return 'bg-red-200';
    default:
      return 'bg-gray-200';
  }
};

export default ListingCard;
