import React, { useEffect, useState } from 'react';
import { Checkbox } from '@mui/material';
import { Check, X, Pen } from 'lucide-react'
import { styled } from '@mui/system';

const CustomCheckbox = styled(Checkbox)({
  '& .MuiSvgIcon-root': { fontSize: 28 }, 
  '&.Mui-checked': {
    color: '#48b574',
  },
});

interface ListingCardProps {
  title?: string;
  companyName?: string;
  description?: string;
  status?: string;
  // POSSIBILITIES FOR STATUS LEGEND
  // WATCHING => GRAY
  // APPLIED => GREEN
  // INTERVIEWING => YELLOW
  // REJECTED => RED

  isApplied?: boolean;

  // double check for type
  applicationDate?: string;
  interviewDate?: string;
  offerDate?: string;
  tags?: string[];
}

const ListingCard: React.FC<ListingCardProps> = ({ title, companyName, description, status, isApplied }) => {
  const [applied, setApplied] = useState(false);

  // useEffect(() => {
  //   setApplied(isApplied);
  // }, [isApplied]);

  const handleApply = () => {
    setApplied(!applied);
  };

  return (
<div className="relative mx-10 mt-4.5">
  <div className="flex items-center justify-between bg-main-gray p-4 rounded-2xl shadow-2xl w-full h-[70px] relative">
    <div className="flex items-center justify-center flex-1 w-[80%] relative">
      <div className="absolute top-0 right-0 w-2.5 h-full bg-main-gray z-10"></div>
    </div>
    <div className="flex items-center justify-center flex-1 w-[80%] relative">
      <div className="absolute top-0 left-0 w-2.5 h-full bg-main-gray z-10"></div>
    </div>
    <div className="flex items-center justify-center flex-1">
      <div className="flex items-center justify-center bg-green-200 rounded-full w-8 h-8 ml-1 shadow-xl"></div>
    </div>
    <div className="flex items-center justify-center flex-1">
      <CustomCheckbox
        checked={applied}
        onChange={handleApply}
        inputProps={{ 'aria-label': 'controlled' }}
        className="ml-1.5"
      />
    </div>
    <div className="absolute right-5 top-1/2 transform -translate-y-1/2 flex items-center">
      <button  className="flex items-center justify-center bg-main-green rounded-full w-5 h-5 ml-1 shadow-md">
        <Pen size={20}/>
      </button>
      <div className="flex items-center justify-center bg-red-400 rounded-full w-5 h-5 ml-1 shadow-md cursor-pointer">
        <CloseIcon size={20} />
      </div>
    </div>
  </div>
</div>
  );
};

export default ListingCard;
