import React, { useState } from 'react';
import { Box, Fab } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import CheckIcon from '@mui/icons-material/Check';
import ListingCardGrid from './ListingCardGrid';

const API_URL = 'http://localhost:8080/jobs'; // Change at production

interface Listing {
  title: string;
  company: string;
  isChecked: boolean;
  isEditing: boolean;
  color: 'red' | 'green' | 'yellow';
}

interface ListingCardGridProps {
  listings: Listing[];
}

const NewListingForm: React.FC = () => {
  const [isAdding, setIsAdding] = useState(false);

  const handleAddListing = async () => {
    setIsAdding(true);

    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: 'New Job',
          company: 'New Company',
          isChecked: false,
          isEditing: false,
          color: 'yellow',
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('New listing added', data);
    } catch (error) {
      console.error('Error adding new listing:', error);
    }
  };

  return (
    <Box>
      <ListingCardGrid />
      <Fab
        color="primary"
        aria-label="add"
        sx={{
          position: 'fixed',
          bottom: 40,
          right: 40,
          backgroundColor: 'white',
          border: '4px solid #67FFA4',
          color: 'black',
          '&:hover': {
            backgroundColor: 'white',
          },
        }}
      >

        {
          isAdding ? (
            <CheckIcon sx={{ color: 'black' }} 
              onClick={handleAddListing}
            />
          ) : (
            <AddIcon sx={{ color: 'black' }} />
          )
        }
      </Fab>
    </Box>
  );
};

export default NewListingForm;
